Properties.Octopus.Action.Script.ScriptBody:
if($IsEnabled -eq "True")
{
Write-Output "Starting Backup the Azure table 'https://$sourceStorageAccoutName.table.core.windows.net/$sourceTableName' to the Blob 'https://$destinationStorageAccoutName.blob.core.windows.net/$destinationBlobContainer'"

& "${Env:ProgramFiles(x86)}\Microsoft SDKs\Azure\AzCopy\azCopy.exe" `
    /Source:https://$sourceStorageAccoutName.table.core.windows.net/$sourceTableName/ `
    /Dest:https://$destinationStorageAccoutName.blob.core.windows.net/$sourceStorageAccoutName-$sourceTableName/ `
    /SourceKey:$sourceStorageAccountKey `
    /Destkey:$destinationStorageAccountKey `
    /y

Write-Output "Backup Completed"
}
else
{
    Write-Output "This Step is disabled"
}
