Properties.Octopus.Action.Script.ScriptBody:
# Running outside octopus
param(
    [string]$DllFilePaths,
    [string]$IncludeCodepath,
    [string]$Version,
    [string]$IsFramework64,
    [string]$Uninstall
)

$ErrorActionPreference = "Stop" 

function Get-Param($Name, [switch]$Required, $Default) {
    $result = $null

    if ($OctopusParameters -ne $null) {
        $result = $OctopusParameters[$Name]
    }

    if ($result -eq $null) {
        $variable = Get-Variable $Name -EA SilentlyContinue   
        if ($variable -ne $null) {
            $result = $variable.Value
        }
    }

    if ($result -eq $null) {
        if ($Required) {
            throw "Missing parameter value $Name"
        } else {
            $result = $Default
        }
    }

    return $result
}

& {
    param(
        [string]$DllFilePaths,
        [string]$IncludeCodepath,
        [string]$Version,
        [string]$IsFramework64,
        [string]$Uninstall
    ) 

    $isUninstall = $($Uninstall.ToLower() -eq 'true')

    Write-Host "COM Component - Register"
    Write-Host "DllFilePaths: $DllFilePaths"

    $DllFilePaths.split(";") | ForEach {
        
         $dllFilePath = $_.Trim();

        if(!(Test-Path "$dllFilePath"))
        {
            Write-Host "FILE NOT FOUND $dllFilePath." -ForegroundColor Yellow;
            return;
        }

        Write-Host "Attempting to register $dllFilePath"
    
        $pinfo = New-Object System.Diagnostics.ProcessStartInfo

        $folderPathVersion = "v2.0.50727"
        $folderPathFramework = "Framework"

        if ($Version -eq 'V4') { 
            $folderPathVersion = "v4.0.30319"
        }

        if ($IsFramework64 -eq 'yes') { 
            $folderPathFramework = "Framework64"
        }

        Write-Host "Registering with: $env:windir\Microsoft.NET\$folderPathFramework\$folderPathVersion\RegAsm.exe"

        $pinfo.FileName = "$env:windir\Microsoft.NET\$folderPathFramework\$folderPathVersion\RegAsm.exe"

    
        $pinfo.RedirectStandardError = $true
        $pinfo.RedirectStandardOutput = $true
        $pinfo.UseShellExecute = $false

        if ($IncludeCodepath -eq "yes") {
            Write-Host "Registering with codebase parameter to specify dll location"
            $pinfo.Arguments = "$dllFilePath /codebase"
        }
        else {
            $pinfo.Arguments = "$dllFilePath"
        }

        if($isUninstall){
            $pinfo.Arguments = "$pinfo.Arguments /u"
        }
        
        $p = New-Object System.Diagnostics.Process

        $p.StartInfo = $pinfo

        Write-Host "Command:"
        Write-Host "$pinfo.FileName $pinfo.Arguments"
        
        if ($p.Start())
        {
            Write-Host $p.StandardOutput.ReadToEnd()

            if($p.ExitCode -ne 0)
            {
                
                Write-Host "FAILED $($p.ExitCode) - Register" -ForegroundColor Red 
                Write-Host $p.StandardError.ReadToEnd()
                                
                return $false
            }
            
            Write-Host "SUCCESS- Register" -ForegroundColor Green 
        }  
    }

 } `
 (Get-Param 'DllFilePaths' -Required) `
 (Get-Param 'IncludeCodepath' -Required) `
 (Get-Param 'Version' -Required) `
 (Get-Param 'Uninstall' -Required)
